const INITIAL_STATE = {
    loading: true
}

export default (state=INITIAL_STATE, action)=> {
    switch (action.type) {
        case 'UPDATE_LOADING':
            return {
                ...state, loading: action.payload
            }
        default:
            return state;
    }
}
